import React, {useEffect, useState} from 'react';
import './Login.css'

 function Login(){

    const [user, setUser] = useState(null)

    const submitHandler = (event) => {
        fetch(`http://localhost:9000/login/${encodeURIComponent(event.currentTarget.elements.email.value)}/${encodeURIComponent(event.currentTarget.elements.password.value)}`)
        .then(response => response.json())
        .then(data => {
            setUser(data)
        })
    }

    useEffect(() => {
        console.log(user)
    }, [user])

    return (
        <div className='login'>
            <div className='background'>
                <form className='loginForm' onSubmit={submitHandler}>
                    <p className='title'>Log In</p>
                    <input type='email' placeholder='Email Address' className='email' required></input>
                    <input type='password' placeholder='Password' className='pass' required></input>
                    <button className='submit'>Log In</button>
                </form>
            </div>
        </div>
    )
}

export default Login;
