import React from "react";
import './Profile.css'

function Profile({user}) {
    const calcNutrient = (user, index) => {
        let total = 0;
        user.foods.map((value, key) => {
            total += value.foodNutrients[index].value
            return total
        })
        return total
    }

    const calcCalories = (user) => {
        let totalP = 0;
        let totalC = 0;
        let totalF = 0;
        user.foods.map((value, key) => {
            totalP += value.foodNutrients[0].value
            totalF += value.foodNutrients[1].value
            totalC += value.foodNutrients[2].value
            return null
        })
        return (4*totalP + 9*totalF + 4*totalC)
    }

    if (user == null) {
        return (
            <div className="profile">
                <p className="error">Log in to access this feature</p>
            </div>
        )
    }
    else {
        return (
            <div className="profile">
                <p className="calories">Total Calories: {calcCalories(user)}</p>
                <ul className="macros">
                    <li>Total Protein: {calcNutrient(user, 0)}g</li>
                    <li>Total Fat: {calcNutrient(user, 1)}g</li>
                    <li>Total Carbohydrates: {calcNutrient(user, 2)}g</li>
                </ul>
                <ul className="vitamins">

                </ul>
                <ul className="minerals">
                    <li>Calcium: {calcNutrient(user, 10)}mg</li>
                    <li>Sodium: {calcNutrient(user, 15)}mg</li>
                    <li>Potassium: {user.foods[0].foodNutrients[14].nutrientId}mg</li>
                    <li>Magnesium: {user.foods[0].foodNutrients[12].nutrientId}mg</li>
                    <li>Phosphorous: {user.foods[0].foodNutrients[13].nutrientId}mg</li>
                    <li>Iron: {user.foods[0].foodNutrients[11].nutrientId}mg</li>
                    <li>Zinc: {user.foods[0].foodNutrients[16].nutrientId}mg</li>
                    <li>Copper: {user.foods[0].foodNutrients[17].nutrientId}mg</li>
                    <li>Selenium: {user.foods[0].foodNutrients[18].nutrientId}ug</li>
                </ul>
            </div>
        )
    }
}

export default Profile
