import React, {useState} from 'react';
import './Signup.css'

 function Signup(){

    const [input, setInput] = useState({email: '', password: '', gender: '', age: 0, height: 0, weight: 0, activity: ''})
    
    const submitHandler = (event) => {
        event.preventDefault();
        let activityLevel;
        if (event.currentTarget.elements.activity.value === 'Sedentary') {
            activityLevel = 1.2;
        }
        else if (event.currentTarget.elements.activity.value === 'Lightly Active') {
            activityLevel = 1.375;
        }
        else if (event.currentTarget.elements.activity.value === 'Moderately Active') {
            activityLevel = 1.55;
        }
        else if (event.currentTarget.elements.activity.value === 'Very Active') {
            activityLevel = 1.725;
        }
        else {
            activityLevel = 1.9;
        }
        fetch(`http://localhost:9000/signup/${encodeURIComponent(event.currentTarget.elements.email.value)}/${encodeURIComponent(event.currentTarget.elements.password.value)}/${encodeURIComponent(event.currentTarget.elements.gender.value)}/${encodeURIComponent(event.currentTarget.elements.age.value)}/${encodeURIComponent(event.currentTarget.elements.height.value)}/${encodeURIComponent(event.currentTarget.elements.weight.value)}/${encodeURIComponent(activityLevel)}`)
    }

    return (
        <div className='signup'>
            <div className='background'>
                <form className='signupForm' onSubmit={submitHandler}>
                    <p className='title'>Sign Up</p>
                    <input type='email' placeholder='Email Address' className='email' id='email' required></input>
                    <input type='password' placeholder='Password' className='pass' id='password' required></input>
                    <select className='gender' id='gender' required>
                        <option hidden disabled selected value>Select a Gender</option>
                        <option value="Male">Male</option>
                        <option value="Female">Female</option>
                    </select>
                    <input type="number" placeholder='Age in Years' className='age' id='age' required></input>
                    <input type="number" placeholder='Height in Inches' className='height' id='height' required></input>
                    <input type="number" placeholder='Weight in Pounds' className='weight' id='weight' required></input>
                    <select className='activity' id='activity' required>
                        <option hidden disabled selected value>Select an Activity Level</option>
                        <option>Sedentary</option>
                        <option>Lightly Active</option>
                        <option>Moderately Active</option>
                        <option>Very Active</option>
                        <option>Extra Active</option>
                    </select>
                    <button className='submit'>Sign Up</button>
                </form>
            </div>
        </div>
    )
}

export default Signup;
