import React, {useEffect, useState} from 'react';
import './Searchbar.css'

 function Searchbar(){

    const [searchResults, setSearchResults] = useState(null)
    const [suggestions, setSuggestions] = useState(null)
    const [searchInput, setSearchInput] = useState('');

    const searchHandler = (event) => {
        setSearchInput(event.target.value)
        fetch(`http://localhost:9000/api/${encodeURIComponent(event.target.value)}`)
        .then(response => response.json())
        .then(data => {
            setSuggestions(data)
        })
    }

    const buttonHandler = (index) => {
        console.log(index)
        console.log(suggestions.foods[index].description)
        setSearchInput(suggestions.foods[index].description)
    }

    const submitHandler = (e) => {
        e.preventDefault()
        fetch(`http://localhost:9000/api/${encodeURIComponent(e.currentTarget.elements.textInput)}`)
        .then(response => response.json())
        .then(data => {
            setSuggestions(data)
        })
    }

    useEffect(() => {
        console.log(suggestions)
    }, [suggestions])

    if (suggestions == null) {
        return (
            <>
                <div className="searchBar">
                    <form className="searchInput" onSubmit={submitHandler}>
                        <input type="text" placeholder='Enter a food' onChange={searchHandler} value={searchInput} className="search" id="textInput"/>
                        <button className='searchButton'>Search</button>
                    </form>
                </div>
                <div className="data">
                        
                </div>
                <div className="results">

                </div>
            </>
        )
    }
    else {
        return (
            <>
                <div className="searchBar">
                    <form className="searchInput">
                        <input type="text" placeholder='Enter a food' onChange={searchHandler} className="search"/>
                        <button className='searchButton'>Search</button>
                    </form>
                </div>
                <div className="data">
                    {suggestions.foods.map((value, key) => {
                        return (
                            <button className='dataItem' onClick={() => {buttonHandler(key)}}>
                                <p>{value.description}</p>
                            </button>
                        );
                    })}
                </div>
                <div className="results">

                </div>
            </>
        )
    }
}

export default Searchbar;
